O = (0,0);
P = (2,0);
Q = (1,sqrt(21)/2);
A = intersectionpoint(O--Q,Circle(Q,1.5));
B = intersectionpoint(Q--interp(P,Q,2),Circle(Q,1.5));

draw(Circle(O,1));
draw(Circle(P,4));
draw(Circle(Q,1.5));
draw(O--Q);
draw(P--B);

label("$r$", (Q + A)/2, NW);
label("$r$", (Q + B)/2, SW);
