size(10cm); // ADJUST
import olympiad;

// Point coordinates
pair r = (0, 0);
pair q = (-8, 0);
pair p = 12 * dir(60);
pair s = (24, 0);
pair t = (p.x, 0);

// draw lines
draw(p--s--r--p--q--r);
draw(p--t);

// labels
